import { useState, useEffect } from "react";
import './App.css';

let quotesData = [];
let ran

function App() {
  const [quotesData, setQuotesData] = useState([]);
  const [randomQuote, setRandomQuote] = useState(null);
  const [randomPhoto, setRandomPhoto] = useState("");

  useEffect(() => {
    fetch("https://dummyjson.com/quotes")
      .then(res => res.json())
      .then(data => {
        // console.log(data);
        // console.log(data.quotes[0]);
        setQuotesData(data.quotes);
        data.quotes.length > 0 ? setRandomQuote(data.quotes[Math.floor(Math.random() * quotesData.length)]) : console.log("No"); 
      })
      .catch(error => console.error("Error:", error));

      fetch("http://localhost:5000/api/photo")
        .then(res => {
          if (!res.ok){
            throw new Error("Network response was not ok");
          }
          return res.json();
        })
        .then(data => {
          console.log("Data from backend: ", data.urls.full);

          setRandomPhoto(data.urls.full);
        })
        .catch(error => console.error("Error: ", error ));
  }, []);

    const getRandomIndex = () => {
      if (quotesData.length > 0){
        const randomIndex = Math.floor(Math.random() * quotesData.length);
        setRandomQuote(quotesData[randomIndex]);
        console.log(setRandomQuote);
      } else {
        console.warn("Quotes not loaded yet.");
        return null;
      }

      fetch("http://localhost:5000/api/photo")
      .then(res => {
        if (!res.ok){
          throw new Error("Network response was not ok");  
        }
        return res.json();
      })
      .then(data => {
        console.log("Data from backend: ", data.urls.full);
        setRandomPhoto(data.urls.full);
      })
      .catch(error => console.error("Error: ", error ));
    }

  return (
    <div className="body flex" 
          style={{ 
            background:`url(${randomPhoto})`, 
            backgroundSize:"cover",
            backgroundPosition: "center",
            transition: "background-image 0.5s ease-in-out" 
          }}>
      <div className="quote-box flex" id="quote-box">

        <div className="quote-border flex" id="text">
          {randomQuote ? <p>{randomQuote.quote}</p> : ""}
        </div>

        <div className="authorContainer"  id="author">
          {randomQuote ? <p className="author">{randomQuote.author}</p> : ""}
        </div>

        <div className="buttons">
          <button id="new-quote" onClick={getRandomIndex}>More Quotes!</button>
        </div>

      </div>
    </div>
  );
}

export default App;
