import { useState, useEffect } from "react";
import './App.css';

let quotesData = [];

function App() {
  const [quotesData, setQuotesData] = useState([]);
  const [randomQuote, setRandomQuote] = useState(null);

  useEffect(() => {
    fetch("https://dummyjson.com/quotes")
      .then(res => res.json())
      .then(data => {
        // console.log(data);
        // console.log(data.quotes[0]);
        setQuotesData(data.quotes);
      })
      .catch(error => console.error("Error:", error));
  }, []);

  const randomIndex = Math.floor(Math.random() * quotesData.length);
  setRandomQuote(quotesData[randomIndex]);

    const getRandomIndex = () => {
      if (quotesData.length > 0){
        const randomIndex = Math.floor(Math.random() * quotesData.length);
        setRandomQuote(quotesData[randomIndex]);
        console.log(setRandomQuote);
      } else {
        console.warn("Quotes not loaded yet.");
        return null;
      }
    }

  return (
    <div className="body flex">
      <div className="quote-container flex">

        <div className="quoteBox flex">
          {randomQuote ? <p>{randomQuote.quote}</p> : ""}
        </div>

        <div className="authorContainer">
          {randomQuote ? <p className="author">{randomQuote.author}</p> : ""}
        </div>

        <div className="buttons">
          <button onClick={getRandomIndex}>Next Quote</button>
        </div>

      </div>
    </div>
  );
}

export default App;
