    })
    .catch(error => console.error("Error:", error));

    const getRandomIndex = () => {
      return randomIndex = data.quotes[Math.floor(Math.random() * data.quotes.length)];
    }

  return (
    <div className="body flex">
      <div className="quote-container flex">

      </div>
    </div>
  );
}

export default App;
