import { useState, useEffect } from "react";
import './App.css';

function App() {
  const [quotesData, setQuotesData] = useState([]);
  const [randomQuote, setRandomQuote] = useState(null);

  useEffect(() => {
    fetch("https://dummyjson.com/quotes")
      .then(res => res.json())
      .then(data => {
        setQuotesData(data.quotes);
      })
      .catch(error => console.error("Error:", error));
  }, []);

  const getRandomQuote = () => {
    if (quotesData.length > 0) {
      const randomIndex = Math.floor(Math.random() * quotesData.length);
      setRandomQuote(quotesData[randomIndex]);
    } else {
      console.warn("Quotes not loaded yet.");
    }
  };

  return (
    <div className="body flex">
      <div className="quote-container flex">
        <button onClick={getRandomQuote}>Get Random Quote</button>
        {randomQuote && (
          <div className="quote">
            <p>{randomQuote.quote}</p>
            <p><em>- {randomQuote.author}</em></p>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
